INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_UNIT_HELP_MUSLIM_CAVALRY', 'Unique Songhai mounted unit. Mandekalu Cavalry starts with the Siege promotion, unlike the Knight it replaces.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_UNIT_HELP_SAMURAI', 'Powerful Medieval soldier unique to the the Japanese. Samurai can attack twice per turn, and are stronger than the Longswordsmen they replace.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_CIV5_BABYLON_BOWMAN_HELP', 'Unique Babylonian archer unit. The Bowman is stronger than the Archer it replaces, and move faster in deserts.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_CIV5_DENMARK_BERSERKER_HELP', 'Soldier of the Medieval era unique to Denmark. The Berserker starts with the Charge promotion, unlike the Longswordsman it replaces.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_UNIT_HELP_PANZER', 'Mobile unit unique to Germany. The Panzer has higher strength, speed, and visibility than the Tank it replaces.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_UNIT_HELP_JAGUAR', 'Early-game soldier unique to the Aztecs. The Jaguar heals damage when it kills an enemy, and fights more effectively in Jungle than the Warrior it replaces.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_UNIT_HELP_MINUTEMAN', 'Renaissance Vanguard Unit unique to the Americans. It is stronger than the Skirmisher it replaces, sees farther, and moves faster in rough terrain.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_UNIT_HELP_MUSKETEER', 'One of the first gunpowder units of the game, unique to the French. Musketeers have a combat bonus outside of friendly territory, unlike the Musketman it replaces.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_UNIT_IROQUOIAN_MOHAWKWARRIOR_STRATEGY', 'Soldier unique to the Iroquois. The Mohawk Warrior fights stronger in Forest and Jungle than the Swordsman it replaces.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_UNIT_OTTOMAN_JANISSARY_STRATEGY', 'Soldier unique to the Ottomans. The Janissary is much stronger on the attack and heals after kills, unlike the Musketman it replaces.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_PROMOTION_MEDIC_GENERAL_HELP', 'Units within 1 tile [COLOR_POSITIVE_TEXT]Heal 4 Additional HP[ENDCOLOR] per turn.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_CIV5_MONGOLIA_KESHIK_HELP', 'The unique Mongolian replacement for the Knight moves faster, is ranged, and does not share the Knight''s vulnerabilities to Cities or Pikemen. The Keshik also earns experience more quickly.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_UNIT_HELP_FOREIGN_LEGION', 'Basic Industrial Era soldier unique to the French. The Foreign Legion costs much less than the Infantry it replaces.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_UNIT_HELP_ROMAN_LEGION', 'Powerful Roman soldier of the Classical Era with a bonus against Cities. The Legion has higher strength than the Swordsman it replaces, extra defense against ranged attacks, and can build roads at twice the speed of Workers.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_UNIT_HELP_INDIAN_WAR_ELEPHANT', 'Powerful mobile unit unique to India. Unlike the Horseman it replaces, the War Elephant is ranged, does not require horses, and has no vulnerability to Spearmen.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_BUILD_MOAI_HELP', '+3 [ICON_CULTURE] Culture.[NEWLINE]+1 [ICON_CULTURE] Culture per adjacent Moai.[NEWLINE][ICON_LOCKED] Must be next to Coast.', '', '');
